import { v4 as uuid } from "uuid";
import userService from "../services/user";

const initialState = {
  helperMessages: [],
  notifications: [],
};

const notificationReducer = (state = initialState, action) => {
  let newState;
  switch (action.type) {
    case "SET_MESSAGE":
      newState = { ...state };
      newState.helperMessages.push(action.message);
      console.log("newState", newState);
      return newState;
    case "SET_NOTIFICATION":
      newState = { ...state };
      return newState.notifications.push(action.content);
    case "CLEAR_NOTIFICATION":
      newState = { ...state };
      return newState.notifications.filter((n) => n.id !== action.content.id);
    default:
      return state;
  }
};

export const setNotification = (message, type) => {
  return (dispatch) => {
    const id = uuid();
    dispatch({
      type: "SET_NOTIFICATION",
      content: {
        id,
        message,
        type,
      },
    });
  };
};

export const clearNotification = (id) => {
  return (dispatch) => {
    dispatch({
      type: "CLEAR_NOTIFICATION",
      content: {
        id,
      },
    });
  };
};

export const readHelperMessage = (type) => {
  return (dispatch) => {
    dispatch({
      type: "SET_MESSAGE",
      message: type,
    });
  };
};

export const hideHelperMessage = (userId, type) => {
  return async (dispatch) => {
    try {
      await userService.readMessage(userId, type);
      dispatch({
        type: "SET_MESSAGE",
        message: type,
      });
    } catch (e) {
      dispatch({
        type: "SET_NOTIFICATION",
        content: {
          message: e.response.data.error,
          type: "error",
        },
      });
    }
  };
};

export default notificationReducer;
