import React from "react";
import { connect } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { OutsideAlerter } from "../../helpers/hooks";
import { logoutUser } from "../../reducers/userReducer";
import {
  setModal,
  setSidebar,
  setDropdown,
} from "../../reducers/navigationReducer";
import Burger from "./Burger";
import Menu from "./Menu";

const NavigationBar = ({
  setModal,
  setDropdown,
  logoutUser,
  user,
  navigation,
}) => {
  let history = useHistory();

  const handleCalculatorClick = (e) => {
    e.preventDefault();
    setModal("userType");
  };

  const handleDropdownClick = (e) => {
    e.preventDefault();
    setDropdown("username");
  };

  const handleLoginClick = (e) => {
    e.preventDefault();
    history.push("/login");
  };

  const handleLogout = (e) => {
    e.preventDefault();
    logoutUser();
    history.push("/");
  };

  return (
    <div className="navbar sticky p0 flex-row justify-c">
      <div className="navbar-side navbar-left flex-row h100 p8 border-p">
        <Burger customClass={"mobile"} />
        Left Navbar
      </div>
      <div className="navbar-main h100 p8 w100 border-p">Main Navbar</div>
      <div className="navbar-side h100 p8 border-p">
        Right Navbar
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
          <path
            fill-rule="evenodd"
            d="M12 3.984c1.125.032 2.062.422 2.813 1.172.75.75 1.124 1.696 1.124 2.836s-.374 2.086-1.124 2.836c-.75.75-1.688 1.125-2.813 1.125-1.125 0-2.062-.375-2.813-1.125-.75-.75-1.124-1.695-1.124-2.836 0-1.14.374-2.086 1.124-2.836.75-.75 1.688-1.14 2.813-1.172zm0 10.032c2.281.03 4.172.421 5.672 1.171s2.281 1.688 2.344 2.813v2.016H3.984V18c.063-1.125.844-2.062 2.344-2.813 1.5-.75 3.39-1.14 5.672-1.171z"
          />
        </svg>
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
          <path
            fill-rule="evenodd"
            d="M20 8V6l-8 5-8-5v2l8 5 8-5zm0-4c.6 0 1 .2 1.4.6.4.4.6.8.6 1.4v12c0 .6-.2 1-.6 1.4a2 2 0 0 1-1.4.6H4a2 2 0 0 1-1.4-.6A2 2 0 0 1 2 18V6c0-.6.2-1 .6-1.4A2 2 0 0 1 4 4h16z"
          />
        </svg>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    user: state.user,
    navigation: state.navigation,
  };
};

const mapDispatchToProps = {
  logoutUser,
  setSidebar,
  setModal,
  setDropdown,
};

export default connect(mapStateToProps, mapDispatchToProps)(NavigationBar);
