import React, { useState } from "react";
import { connect } from "react-redux";
import { TooltipOutsideAlerter } from "../../utils/hooks";

const Tooltip = ({ message }) => {
  const [show, setShow] = useState(false);

  return (
    <TooltipOutsideAlerter setShow={setShow}>
      <div className="relative mb8">
        {show && (
          <div className="tooltip-msg absolute bg-black r p8 font-white white text-center fade-in">
            {message}
          </div>
        )}
        <button
          type="button"
          className="tooltip-btn"
          onClick={() => setShow(!show)}
        >
          <span className="f12">?</span>
        </button>
      </div>
    </TooltipOutsideAlerter>
  );
};

export default connect(null, null)(Tooltip);
