import React from "react";
import Button from "./Button";
import ArrowUpIcon from "../../styles/svg/arrow-up.svg";

const ScrollToTop = () => {
  const handleClick = (e) => {
    try {
      window.scroll({
        top: 0,
        left: 0,
        behavior: "smooth",
      });
    } catch (e) {
      window.scrollTo(0, 0);
    }
  };

  return (
    <Button
      extraClass={"button-transp-p align-c justify-c"}
      onClick={handleDropdownClick}
      iconUrl={ArrowUpIcon}
      iconColor={"white"}
    />
  );
};

export default ScrollToTop;
