import React from "react";
import { connect } from "react-redux";
import { Form, Field } from "react-final-form";
import { setMessage } from "../reducers/contactReducer";
import {
  required,
  isEmail,
  composeValidators,
} from "../utils/formValidatorHelper";
import FinalFormField from "./Shared/FinalFormField";

const Contact = ({ setMessage }) => {
  const onSubmit = (values) => {
    setMessage(values);
  };

  return (
    <>
      <h1 className="f24 bold mt16 mb16">Contact Us</h1>
      <div className="r bs-3 bg-1 p20 mb20">
        <Form
          onSubmit={onSubmit}
          render={({ handleSubmit, rest }) => (
            <form onSubmit={handleSubmit}>
              <FinalFormField
                label={"Full Name"}
                fieldName={"fullName"}
                validators={[required, maxLength(50)]}
                placeholder={"Full Name"}
                fieldType={"text"}
              />
              <FinalFormField
                label={"Company"}
                fieldName={"fullName"}
                validators={[required, maxLength(100)]}
                placeholder={"Full Name"}
                fieldType={"text"}
              />
              <FinalFormField
                label={"Full Name"}
                fieldName={"fullName"}
                validators={[required, maxLength(100)]}
                placeholder={"Full Name"}
                fieldType={"text"}
              />
            </form>
          )}
        />
      </div>
    </>
  );
};

const mapStateToProps = (state) => {
  return {
    isFetching: state.contact.isFetching,
  };
};

const mapDispatchToProps = {
  setMessage,
};

export default connect(mapStateToProps, mapDispatchToProps)(Contact);
