import React from "react";
import { connect } from "react-redux";
import { clearNotification } from "../../../reducers/notificationReducer";
import Button from "../Button";
import CloseIcon from "../../../styles/svg/close.svg";
import { CONSTANTS } from "../../../static/constants";
import MessageIcon from "../../../styles/svg/message.svg";
import SuccessIcon from "../../../styles/svg/tick.svg";
import ErrorIcon from "../../../styles/svg/error.svg";

const NotificationAlert = ({ notification, clearNotification }) => {
  const handleCloseNotification = (e) => {
    e.preventDefault();
    clearNotification(notification.id);
  };

  let notificationType;
  let icon;
  switch (notification.type) {
    case CONSTANTS.NOTIFICATION.MESSAGE:
      notificationType = "message";
      icon = MessageIcon;
      break;
    case CONSTANTS.NOTIFICATION.SUCCESS:
      notificationType = "success";
      icon = SuccessIcon;
      break;
    case CONSTANTS.NOTIFICATION.ERROR:
      notificationType = "error";
      icon = ErrorIcon;
      break;
    default:
      notificationType = "message";
      icon = MessageIcon;
  }

  const toTitleCase = (string) => {
    return string.replace(/\w\S*/g, (txt) => {
      return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();
    });
  };

  return (
    <div className="notification p8 mb8 fade-in flex-row align-c relative bs-2 jump border-p">
      <Button
        extraClass={"button-p"}
        onClick={handleCloseNotification}
        icon={CloseIcon}
        extraStyle={{
          alignItems: "center",
          justifyContent: "center",
          position: "absolute",
          right: "4px",
        }}
      />
      <div className="mr36">
        <div
          style={{ background: `url(${icon})`, height: "32px", width: "32px" }}
        ></div>
        <span className={`${notificationType} bold ml8`}>
          {toTitleCase(notificationType)}:
        </span>
        <span className="font-white">
          {typeof notification.message === "string"
            ? ` ${notification.message}`
            : ` ${notification.statusText}`}
        </span>
      </div>
    </div>
  );
};

// const mapStateToProps = (state) => {
//   return {
//     notification: state.notification,
//   };
// };

const mapDispatchToProps = {
  clearNotification,
};

export default connect(null, mapDispatchToProps)(NotificationAlert);
