import React, { useState, useEffect, useRef } from "react";
import { connect } from "react-redux";

const Tooltip = ({ message }) => {
  const [show, setShow] = useState(false);

  const TooltipOutsideAlerter = (props) => {
    const wrapperRef = useRef(null);

    const handleClickOutside = (e) => {
      if (!(wrapperRef.current && wrapperRef.current.contains(e.target))) {
        setShow(false);
      }
    };

    useEffect(() => {
      // Bind the event listener
      document.addEventListener("mousedown", handleClickOutside);
      return () => {
        // Unbind the event listener on clean up
        document.removeEventListener("mousedown", handleClickOutside);
      };
    });

    return <div ref={wrapperRef}>{props.children}</div>;
  };

  return (
    <TooltipOutsideAlerter>
      <div className="relative mb8">
        {show && (
          <div className="tooltip-msg absolute bg-black r p8 font-white white text-center fade-in">
            {message}
          </div>
        )}
        <button
          type="button"
          className="tooltip-btn"
          onClick={() => setShow(!show)}
        >
          ?
        </button>
      </div>
    </TooltipOutsideAlerter>
  );
};

export default connect(null, null)(Tooltip);
