import React from "react";
import { connect } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { OutsideAlerter } from "../../helpers/hooks";
import { logoutUser } from "../../reducers/userReducer";
import {
  setModal,
  setSidebar,
  setDropdown,
} from "../../reducers/navigationReducer";
import Burger from "./Burger";
import Menu from "./Menu";

const NavigationBar = ({
  setModal,
  setDropdown,
  logoutUser,
  user,
  navigation,
}) => {
  let history = useHistory();

  const handleCalculatorClick = (e) => {
    e.preventDefault();
    setModal("userType");
  };

  const handleDropdownClick = (e) => {
    e.preventDefault();
    setDropdown("username");
  };

  const handleLoginClick = (e) => {
    e.preventDefault();
    history.push("/login");
  };

  const handleLogout = (e) => {
    e.preventDefault();
    logoutUser();
    history.push("/");
  };

  return (
    <div className="navbar sticky p0 flex-row justify-c">
      <div className="navbar-side navbar-left flex-row h100 p8 border-p">
        <Burger customClass={"mobile"} />
        Left Navbar
      </div>
      <div className="navbar-main h100 p8 w100 border-p">Main Navbar</div>
      <div className="navbar-side h100 p8 border-p">
        Right Navbar
        <svg xmlns="http://www.w3.org/2000/svg" width="172" height="144">
          <g fill="none" fill-rule="evenodd">
            <path
              fill="#030A42"
              fill-opacity=".47"
              d="M86 121.92a9.54 9.54 0 0 1-7.03-2.81 10.4 10.4 0 0 1-3.05-7.03h20.16a10.4 10.4 0 0 1-3.05 7.03 9.54 9.54 0 0 1-7.03 2.81zm30-30L126.08 102v4.92H45.92V102L56 91.92V67.08c0-7.97 1.95-14.77 5.86-20.4 3.9-5.62 9.45-9.37 16.64-11.24v-3.52c0-2.03.7-3.75 2.1-5.15a7.3 7.3 0 0 1 5.4-2.11c2.19 0 3.98.7 5.4 2.1a7.01 7.01 0 0 1 2.1 5.16v3.52c7.19 1.87 12.73 5.66 16.64 11.36 3.9 5.7 5.86 12.47 5.86 20.28v24.84z"
            />
            <rect
              width="40"
              height="50"
              x="104.5"
              y="13.5"
              fill="#E62236"
              stroke="#FFF"
              stroke-width="5"
              rx="10"
            />
            <path
              fill="#FFF"
              fill-rule="nonzero"
              d="M131.34 47.5h-13.4v-2.66l6.33-6.74a15.6 15.6 0 0 0 1.92-2.48 4 4 0 0 0 .63-2.02c0-.87-.22-1.55-.66-2.05-.44-.5-1.07-.74-1.88-.74-.88 0-1.57.3-2.08.9-.5.61-.75 1.4-.75 2.39h-3.9c0-1.2.29-2.28.85-3.26a6.01 6.01 0 0 1 2.41-2.32 7.33 7.33 0 0 1 3.54-.84c2.01 0 3.57.48 4.69 1.45a5.13 5.13 0 0 1 1.67 4.1c0 .96-.25 1.95-.75 2.95-.5 1-1.36 2.17-2.58 3.5l-4.44 4.7h8.4v3.12z"
            />
          </g>
        </svg>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    user: state.user,
    navigation: state.navigation,
  };
};

const mapDispatchToProps = {
  logoutUser,
  setSidebar,
  setModal,
  setDropdown,
};

export default connect(mapStateToProps, mapDispatchToProps)(NavigationBar);
