import React, { useEffect } from "react";
import { connect } from "react-redux";
import { useHistory, useParams } from "react-router-dom";
import { setCashflow, setModal } from "../../reducers/navigationReducer";
import {
  getDashboard,
  preSaveDashboard,
} from "../../reducers/dashboardReducer";
// import CalculatorFormTooltip from "../CalculatorForms/CalculatorFormTooltip";
import {
  cumulativeChartParse,
  annualChartParse,
  tableParse,
  developerCalculation,
  developerMOCCalculation,
  fundingChartParse,
} from "../../utils/developerDashboardDataParser";
import {
  currencyFormatter,
  percentageFormatter,
  IRRCalculation,
} from "../../utils/dashboardHelper";
import { Line, Bar } from "react-chartjs-2";
import Loader from "../Shared/Loader";
import { Icon } from "../Shared/Icon";
import SaveIcon from "../../styles/svg/save.svg";
import EditIcon from "../../styles/svg/edit.svg";

const DeveloperDashboard = ({
  dashboards,
  getDashboard,
  preSaveDashboard,
  setModal,
  cashflowTable,
  setCashflow,
}) => {
  const id = useParams().id;
  const history = useHistory();

  useEffect(() => {
    if (id && !dashboards.preSave) {
      getDashboard(id);
    }
  }, [id]);

  const handleSave = (e) => {
    e.preventDefault();
    setModal("saveDashboard");
  };

  const handleEdit = (e) => {
    e.preventDefault();
    preSaveDashboard();
    if (id) {
      history.push(`/developer/edit/${id}`);
    } else {
      history.push(`/developer/edit`);
    }
  };

  if (dashboards.isFetching || !dashboards.data[0]) {
    return <Loader />;
  } else {
    const rawData = developerCalculation(dashboards.data[0].values);
    const annualChart = annualChartParse(rawData);
    const cumulativeChart = cumulativeChartParse(rawData);
    const tableData = tableParse(rawData);
    const fundingChart = fundingChartParse(rawData);
    return (
      <>
        <div className="dash-row relative">
          <h2 className="f20 bold mt16 mb16">Developer Dashboard</h2>
          <div className="dash-btns flex-row">
            <button
              type="submit"
              className="form-button-p bs-3 font-white pt4 pb4 flex-row align-c justify-c"
              onClick={handleSave}
            >
              <Icon
                size={"20px"}
                url={SaveIcon}
                color={"white"}
                hover={false}
                active={false}
              />
              <span className="ml8">Save</span>
            </button>
            <button
              type="submit"
              className="form-button-s bs-3 font-white pt4 pb4 flex-row align-c justify-c"
              onClick={handleEdit}
            >
              <Icon
                size={"20px"}
                url={EditIcon}
                color={"white"}
                hover={false}
                active={false}
              />
              <span className="ml8">Edit</span>
            </button>
          </div>
        </div>
        <h2 className="f16 bold mt16 mb16">Pre Funding Metrics</h2>
        <div className="dash-row flex-row">
          <table className="r bs-3 bg-1 p20 mb16 o-hidden">
            <thead>
              <tr>
                <th>Total</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>Revenue:</td>
                <td>
                  {currencyFormatter.format(
                    tableData.summaryCashflow[0].totalRevenue
                  )}
                </td>
              </tr>
              <tr>
                <td>Loan Interest:</td>
                <td>{currencyFormatter.format(cardData.mortgageInterest)}</td>
              </tr>
              <tr>
                <td>Profit:</td>
                <td>{currencyFormatter.format(cardData.profit)}</td>
              </tr>
            </tbody>
          </table>
          <table className="r bs-3 bg-1 p20 mb16 o-hidden">
            <thead>
              <tr>
                <th>Avg Per Month</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>Rental Income:</td>
                <td>
                  {currencyFormatter.format(
                    cardData.rentalIncome / cardData.months
                  )}
                </td>
              </tr>
              <tr>
                <td>Operating Costs:</td>
                <td>
                  {currencyFormatter.format(cardData.opex / cardData.months)}
                </td>
              </tr>
              <tr>
                <td>Mortgage Payment:</td>
                <td>
                  {currencyFormatter.format(
                    cardData.mortgagePayment / cardData.months
                  )}
                </td>
              </tr>
            </tbody>
          </table>
          <table className="r bs-3 bg-1 p20 mb16 o-hidden">
            <thead>
              <tr>
                <th>Project Metrics</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>IRR:</td>
                <td>
                  {percentageFormatter.format(
                    IRRCalculation(rawData).postFinance
                  )}
                </td>
              </tr>
              <tr>
                <td>Margin On Cost:</td>
                <td>
                  {percentageFormatter.format(
                    occupierInvestorMOCCalculation(rawData)
                  )}
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </>
    );
  }
};

const mapStateToProps = (state) => {
  return {
    dashboards: state.dashboards,
    cashflowTable: state.navigation.cashflowTable,
    user: state.user,
  };
};

const mapDispatchToProps = {
  setCashflow,
  setModal,
  getDashboard,
  preSaveDashboard,
};

export default connect(mapStateToProps, mapDispatchToProps)(DeveloperDashboard);
