import React from "react";
import { connect } from "react-redux";
import { Form, Field } from "react-final-form";
import { createUser, updateUser, deleteUser } from "../reducers/userReducer";
import { setNotification } from "../reducers/notificationReducer";
import {
  required,
  minLength,
  isEmail,
  composeValidators,
} from "../utils/formValidatorHelper";

const Settings = ({ user, updateUser, deleteUser }) => {
  const userDetails = {
    username: user.data.username,
    oldEmail: user.data.email,
    id: user.data.id,
  };

  const handleEmailChange = async (values) => {
    const confirm = window.confirm(
      `Change email from ${userDetails.oldEmail} to ${values.newEmail}?`
    );
    if (confirm) {
      await updateUser({
        newEmail: values.newEmail,
        ...userDetails,
      });
    }
  };

  const handlePasswordChange = async (values) => {
    const { oldPassword, newPassword, checkPassword } = values;
    await updateUser({
      oldPassword,
      newPassword,
      checkPassword,
      ...userDetails,
    });
  };

  const handleDelete = async ({ password }) => {
    const confirm = window.confirm(`Delete ${userDetails.username}?`);
    if (confirm) {
      await deleteUser(password, user.id);
    }
  };

  return (
    <>
      <h1 className="f24 bold mt16 mb16">Settings</h1>
      <Form
        onSubmit={handleEmailChange}
        render={({ handleSubmit, values }) => (
          <form onSubmit={handleSubmit}>
            <h2 className="f20 bold mt16 mb16">Change Email</h2>
            <div className="r bs-3 bg-1 p20 mb20">
              <label htmlFor="old-email" className="f16 mb8">
                Existing Email
                <span className="font-red f12 bold ml4">*</span>
              </label>
              <div className="relative mb20">
                <input
                  id="old-email"
                  className="form-input bs-1 w100"
                  placeholder={user.data.email}
                  type="email"
                  disabled
                />
              </div>
              <label htmlFor="new-email" className="f16 mb8">
                New Email
                <span className="font-red f12 bold ml4">*</span>
              </label>
              <Field
                name="newEmail"
                validate={composeValidators(required, minLength(3), isEmail)}
              >
                {({ input, meta }) => (
                  <div className="relative mb20">
                    <input
                      id="new-email"
                      className="form-input bs-1 w100"
                      placeholder="new@email.com"
                      type="email"
                      {...input}
                    />
                    {meta.error && meta.touched && (
                      <span className="form-error f10">{meta.error}</span>
                    )}
                  </div>
                )}
              </Field>
              <button
                className="form-button-p font-white bs-2 pt8 pb8 r"
                type="submit"
              >
                Update
              </button>
            </div>
          </form>
        )}
      />
    </>
  );
};

const mapStateToProps = (state) => {
  return {
    user: state.user,
  };
};

const mapDispatchToProps = {
  createUser,
  updateUser,
  deleteUser,
  setNotification,
};

export default connect(mapStateToProps, mapDispatchToProps)(Settings);
