import React from "react";
import { connect } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { OutsideAlerter } from "../../helpers/hooks";
import { logoutUser } from "../../reducers/userReducer";
import {
  setModal,
  setSidebar,
  setDropdown,
} from "../../reducers/navigationReducer";
import Burger from "./Burger";
import Menu from "./Menu";

const NavigationBar = ({
  setModal,
  setDropdown,
  logoutUser,
  user,
  navigation,
}) => {
  let history = useHistory();

  const handleCalculatorClick = (e) => {
    e.preventDefault();
    setModal("userType");
  };

  const handleDropdownClick = (e) => {
    e.preventDefault();
    setDropdown("username");
  };

  const handleLoginClick = (e) => {
    e.preventDefault();
    history.push("/login");
  };

  const handleLogout = (e) => {
    e.preventDefault();
    logoutUser();
    history.push("/");
  };

  return (
    <div className="navbar sticky p0 flex-row justify-c">
      <div className="navbar-side navbar-left flex-row h100 p8 border-p">
        <Burger customClass={"mobile"} />
        Left Navbar
      </div>
      <div className="navbar-main h100 p8 w100 border-p">Main Navbar</div>
      <div className="navbar-side h100 p8 border-p">
        Right Navbar
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
          <path
            fill-rule="evenodd"
            d="M21 18.98v1.04H3v-1.04l2.02-1.96v-6c0-1.6.45-3 1.36-4.2.9-1.2 2.1-2.04 3.6-2.5v-.34c.04-.56.24-1.03.61-1.4A1.9 1.9 0 0 1 12 2.02c.56 0 1.03.18 1.4.56.38.37.58.84.62 1.4v.33c1.5.47 2.7 1.3 3.6 2.51.91 1.2 1.36 2.6 1.36 4.2v6L21 18.98zM14.02 21a2.08 2.08 0 0 1-.61 1.4 1.9 1.9 0 0 1-1.41.57 1.9 1.9 0 0 1-1.4-.56A2.08 2.08 0 0 1 9.97 21h4.04z"
          />
        </svg>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    user: state.user,
    navigation: state.navigation,
  };
};

const mapDispatchToProps = {
  logoutUser,
  setSidebar,
  setModal,
  setDropdown,
};

export default connect(mapStateToProps, mapDispatchToProps)(NavigationBar);
