import React, { useEffect } from "react";
import { connect } from "react-redux";
import { useHistory, useParams } from "react-router-dom";
import Tooltip from "../Shared/Tooltip";
import Loader from "../Shared/Loader";
import { Icon } from "../Shared/Icon";
import {
  getDashboard,
  preSaveDashboard,
} from "../../reducers/dashboardReducer";
import { setCashflow, setModal } from "../../reducers/navigationReducer";
import {
  cumulativeChartParse,
  tableParse,
  occupierInvestorCalculation,
  cardParse,
  occupierInvestorMOCCalculation,
} from "../../utils/occupierAndInvestorDataParser";
import {
  currencyFormatter,
  percentageFormatter,
  IRRCalculation,
} from "../../utils/dashboardHelper";
import { Line } from "react-chartjs-2";
import { CONSTANTS } from "../../static/constants";
import SaveIcon from "../../styles/svg/save.svg";
import EditIcon from "../../styles/svg/edit.svg";

const OccupierInvestorDashboard = ({
  dashboards,
  getDashboard,
  preSaveDashboard,
  setModal,
  cashflowTable,
  setCashflow,
}) => {
  const id = useParams().id;
  const history = useHistory();

  useEffect(() => {
    if (id && !dashboards.preSave) {
      getDashboard(id);
    }
  }, [id]);

  const handleSave = (e) => {
    e.preventDefault();
    setModal("saveDashboard");
  };

  const handleEdit = (e) => {
    e.preventDefault();
    preSaveDashboard();
    if (id) {
      history.push(`/owner-occupier/edit/${id}`);
    } else {
      history.replace("/owner-occupier/edit");
    }
  };

  if (dashboards.isFetching || !dashboards.data[0]) {
    return <Loader />;
  } else {
    const userType = CONSTANTS.TYPES.OWNEROCCUPIER;
    const rawData = occupierInvestorCalculation(dashboards.data[0].values);
    const chartData = cumulativeChartParse(rawData);
    const tableData = tableParse(rawData);
    const cardData = cardParse(rawData);
    return (
      <>
        <div className="flex-row">
          <h2 className="f20 bold mt16 mb16">Owner Occupier Dashboard</h2>
          <button
            type="submit"
            className="form-button-p bs-3 font-white mt20 pt4 pb4 flex-row align-c justify-c"
            onClick={handleSave}
          >
            <Icon
              size={"20px"}
              url={SaveIcon}
              color={"white"}
              hover={false}
              active={false}
            />
            <span className="ml8">Save</span>
          </button>
          <button
            type="submit"
            className="form-button-s bs-3 font-white mt20 pt4 pb4 flex-row align-c justify-c"
            onClick={handleEdit}
          >
            <Icon
              size={"20px"}
              url={EditIcon}
              color={"white"}
              hover={false}
              active={false}
            />
            <span className="ml8">Edit</span>
          </button>
        </div>
        <h2 className="f16 bold mt16">Cumulative Position</h2>
        <h3 className="f16 mb16">After Funding</h3>
        <table className="r bs-3 bg-1 p20 mb20">
          <th>Total</th>
          <tr>
            Rental Income: {currencyFormatter.format(cardData.rentalIncome)}
          </tr>
          <tr>
            Loan Interest: {currencyFormatter.format(cardData.mortgageInterest)}
          </tr>
          <tr>Profit: {currencyFormatter.format(cardData.profit)}</tr>
        </table>
        <div className="r bs-3 bg-1 p20 mb20">
          <Line data={chartData.data} options={chartData?.options} />
        </div>
      </>
    );
  }
};

const mapStateToProps = (state) => {
  return {
    dashboards: state.dashboards,
    cashflowTable: state.navigation.cashflowTable,
  };
};

const mapDispatchToProps = {
  setCashflow,
  setModal,
  getDashboard,
  preSaveDashboard,
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(OccupierInvestorDashboard);
