import React from "react";
import { Field } from "react-final-form";
import { composeValidators } from "../../utils/formValidatorHelper";
import {
  developerTooltipHelper,
  occupierInvestorTooltipHelper,
} from "../../utils/tooltipHelper";
import { CONSTANTS } from "../../static/constants";

const FinalFormField = ({
  label,
  fieldName,
  type,
  validators,
  placeholder,
  fieldType,
  maxLength,
  min,
  max,
  step,
  prepend,
  append,
  parseType,
  readOnly,
}) => {
  const message =
    type === CONSTANTS.TYPES.DEVELOPER
      ? developerTooltipHelper[fieldName].message
      : occupierInvestorTooltipHelper[fieldName].message;

  const parse = (value) => {
    if (parseType === "parseInt") {
      return isNaN(parseInt(value)) ? "" : parseInt(value);
    }
    return isNaN(parseFloat(value)) ? "" : parseFloat(value);
  };

  const id = `${type}-${fieldName}`;

  return (
    <>
      <label for={id}>
        {label}
        {validators.includes("required") && <span className="font-red">*</span>}
      </label>
      <span aria-label={message} data-balloon-pos="up">
        ?
      </span>
      <Field
        name={fieldName}
        validate={composeValidators(...validators)}
        parse={parse}
      >
        {({ input, meta }) => (
          <div className="relative">
            <input
              id={id}
              className={`form-input w100 ${prepend ? "pl32" : ""} ${
                append ? "pr36" : ""
              }`}
              placeholder={placeholder}
              type={fieldType}
              maxLength={maxLength}
              min={min}
              max={max}
              step={step}
              {...input}
              readOnly={readOnly}
            />
            {prepend && (
              <span className="absolute prepend f16 flex-row justify-c align-c">
                {prepend}
              </span>
            )}
            {append && <span className="absolute append">{append}</span>}
            {meta.error && meta.touched && (
              <span className="form-error">{meta.error}</span>
            )}
          </div>
        )}
      </Field>
    </>
  );
};

export default FinalFormField;
