import React, { useState } from "react";
import { connect } from "react-redux";
import Button from "../Shared/Button";
import ExpandIcon from "../../styles/svg/expand.svg";
import CollapseIcon from "../../styles/svg/collapse.svg";

const MenuItem = ({ heading }) => {
  const [expand, setExpand] = useState(true);

  return (
    <div className="bg2 r bs-1 mb20 w100 flex-col align-c">
      <div className="flex-row w100">
        <Button
          iconSize={"20px"}
          ariaLabel={expand ? "Collapse" : "Expand"}
          dataBalloonPos={"right"}
          extraClass={"button-transp-p align-c bold w100"}
          onClick={() => setExpand(!expand)}
          iconUrl={expand ? CollapseIcon : ExpandIcon}
          iconColor={"white"}
          caption={heading}
        />
      </div>
      <div className="flex-col">
        {expand ? (
          <>
            <Button
              extraClass={"button-p align-c w100 ml16"}
              iconColor={"black"}
              iconUrl={expand ? CollapseIcon : ExpandIcon}
              caption={"Test Caption"}
            />
            <Button
              extraClass={"button-p align-c w100 ml16"}
              iconUrl={expand ? CollapseIcon : ExpandIcon}
              caption={"Test Caption"}
            />
            <Button
              extraClass={"button-p align-c w100 ml16"}
              iconUrl={expand ? CollapseIcon : ExpandIcon}
              caption={"Test Caption"}
            />
          </>
        ) : null}
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    leftSidebarOpen: state.navigation.sidebarOpen.left,
  };
};

const mapDispatchToProps = null;

export default connect(mapStateToProps, mapDispatchToProps)(MenuItem);
