import { v4 as uuid } from "uuid";
import userService from "../services/user";

const initialState = {
  helperMessages: [],
  notifications: [],
};

const notificationReducer = (state = initialState, action) => {
  let newState;
  switch (action.type) {
    case "SET_MESSAGE":
      newState = { ...state };
      newState.helperMessages.push(action.message);
      return newState;
    case "SET_MESSAGES":
      if (action.messages.length) {
        newState = { ...state };
        newState.helperMessages.push(...action.messages);
        return newState;
      } else {
        return state;
      }
    case "SET_NOTIFICATION":
      newState = { ...state };
      newState.notifications.push(action.content);
      return newState;
    case "CLEAR_NOTIFICATION":
      newState = { ...state };
      newState.notifications.filter((n) => {
        console.log(n);
        console.logaction();
        return n.id !== action.id;
      });
      console.log(newState);
      return newState;
    default:
      return state;
  }
};

export const setNotification = (message, type) => {
  return (dispatch) => {
    const id = uuid();
    dispatch({
      type: "SET_NOTIFICATION",
      content: {
        id,
        message,
        type,
      },
    });
  };
};

export const clearNotification = (id) => {
  return (dispatch) => {
    dispatch({
      type: "CLEAR_NOTIFICATION",
      id,
    });
  };
};

export const hideHelperMessage = (userId, type) => {
  return async (dispatch) => {
    try {
      await userService.readMessage(userId, type);
      dispatch({
        type: "SET_MESSAGE",
        message: type,
      });
    } catch (e) {
      dispatch({
        type: "SET_NOTIFICATION",
        content: {
          message: e.response.data.error,
          type: "error",
        },
      });
    }
  };
};

export default notificationReducer;
