import React from "react";
import { connect } from "react-redux";
import Button from "../Shared/Button";

const NotificationAlert = ({ notification, key }) => {
  return (
    <div
      id="notification-alert"
      variant={notification.type === "success" ? "success" : "danger"}
      onClose={() => setShow(key)}
    >
      <Button
        extraClass={"button-p"}
        onClick={handleCloseNotification}
        icon={UserIcon}
        extraStyle={{ alignItems: "center", justifyContent: "center" }}
      />
      <div>{notification.type === "success" ? "Success" : "Error"}</div>
      <p>
        {typeof notification.message === "string"
          ? notification.message
          : notification.statusText}
      </p>
    </div>
  );
};

export default connect(null, null)(NotificationAlert);
