import React from "react";
import { connect } from "react-redux";
import { clearNotification } from "../../reducers/notificationReducer";
import Button from "../Shared/Button";
import CloseIcon from "../../styles/svg/close.svg";

const NotificationAlert = ({ notification, clearNotification }) => {
  const handleCloseNotification = (e) => {
    e.preventDefault();
    clearNotification(n.id);
  };
  return (
    <div className={notification.type === "success" ? "success" : "danger"}>
      <Button
        extraClass={"button-p"}
        onClick={handleCloseNotification}
        icon={CloseIcon}
        extraStyle={{ alignItems: "center", justifyContent: "center" }}
      />
      <div>{notification.type === "success" ? "Success" : "Error"}</div>
      <p>
        {typeof notification.message === "string"
          ? notification.message
          : notification.statusText}
      </p>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    notification: state.notification,
  };
};

const mapDispatchToProps = {
  clearNotification,
};

export default connect(mapStateToProps, null)(NotificationAlert);
