import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getDashboards } from "../reducers/dashboardReducer";
import Loader from "./Shared/Loader";

const SavedDashboards = ({ getDashboards, isFetching, savedDashboards }) => {
  useEffect(() => {
    getDashboards();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  if (isFetching) {
    return <Loader />;
  } else {
    return (
      <>
        <h1 className="f24 bold mt16 mb16">Saved Dashboards</h1>
        <table id="save-overwrite" className="overpayments w100 mb20">
          <thead>
            <tr>
              <th className="h768">Ref</th>
              <th>Description</th>
              <th className="h768">Type</th>
              <th>Created</th>
              <th>Overwrite</th>
            </tr>
          </thead>
          <tbody>
            {savedDashboards.map((d, i) => {
              let type;
              if (d.values.type === "developer") {
                type = "Developer";
              } else if (d.values?.investor) {
                type = "Investor";
              } else {
                type = "Owner Occupier";
              }
              return (
                <tr key={i}>
                  <td className="h768">{i + 1}</td>
                  <td>{d.description}</td>
                  <td className="h768">{type}</td>
                  <td>{formatDate(d.date)}</td>
                  <td>
                    <div className="flex-row">
                      <button
                        type="button"
                        className={`save-opt button-transp-s rt pl16 pr16 flex-row align-c justify-c jump ${
                          saveNew ? "active" : ""
                        }`}
                        onClick={() => setSaveNew(true)}
                      >
                        <Icon
                          size={"20px"}
                          url={SaveIcon}
                          color={"black"}
                          hover={false}
                          active={false}
                        />
                      </button>
                      <button
                        type="button"
                        className={`save-opt button-transp-s rt pl16 pr16 flex-row align-c justify-c jump ${
                          saveNew ? "" : "active"
                        }`}
                        onClick={() => setSaveNew(false)}
                      >
                        <Icon
                          size={"20px"}
                          url={OverwriteIcon}
                          color={"black"}
                          hover={false}
                          active={false}
                        />
                      </button>
                    </div>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </>
    );
  }
};

const mapStateToProps = (state) => {
  return {
    savedDashboards: state.dashboards.savedDashboards,
    isFetching: state.dashboards.isFetching,
  };
};

const mapDispatchToProps = {
  getDashboards,
};

export default connect(mapStateToProps, mapDispatchToProps)(SavedDashboards);
