import React from "react";
import { connect } from "react-redux";
import { Form as FinalForm } from "react-final-form";
import arrayMutators from "final-form-arrays";
import { testDashboard } from "../../../reducers/dashboardReducer";
import { setAccordian, setModal } from "../../../reducers/navigationReducer";
import Loader from "../../Shared/Loader";
// import OccupierInvestorStandardAssumptions from "./OccupierInvestorStandardAssumptions";
// import OccupierInvestorAdvancedAssumptions from "./OccupierInvestorAdvancedAssumptions";

const OccupierForm = ({
  id,
  investor,
  setModal,
  testDashboard,
  dashboards,
  setAccordian,
  navigation,
}) => {
  const onSubmit = (values) => {
    setModal("disclaimer");
    values.investor = investor;
    values.type = "occupierInvestor";
    testDashboard(values);
  };

  if (dashboards.isFetching) {
    return <Loader />;
  } else {
    const initialValues =
      dashboards.preSave || id ? dashboards.data[0].values : null;
    return (
      <>
        <h1 className="f28 bold mt16 mb16">Owner Occupier Assumptions</h1>
        <div>
          <h2 className="f20 bold mt8 mb8">Input Heading 1</h2>
          <div className="r bs-3 bg-1"></div>
        </div>
      </>
    );
  }
};

const mapStateToProps = (state) => {
  return {
    dashboards: state.dashboards,
    navigation: state.navigation,
  };
};

const mapDispatchToProps = {
  testDashboard,
  setAccordian,
  setModal,
};

export default connect(mapStateToProps, mapDispatchToProps)(OccupierForm);
