import React from "react";
import { connect } from "react-redux";
import { clearNotification } from "../../../reducers/notificationReducer";
import Button from "../Button";
import CloseIcon from "../../../styles/svg/close.svg";
import { CONSTANTS } from "../../../static/constants";

const NotificationAlert = ({ notification, clearNotification }) => {
  const handleCloseNotification = (e) => {
    e.preventDefault();
    clearNotification(notification.id);
  };

  let notificationType;
  switch (notification.type) {
    case CONSTANTS.NOTIFICATION.SUCCESS:
      notificationType = "success";
      break;
    case CONSTANTS.NOTIFICATION.ERROR:
      notificationType = "error";
      break;
    default:
      notificationType = "success";
  }

  const toTitleCase = (string) => {
    return string.replace(/\w\S*/g, (txt) => {
      return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();
    });
  };

  return (
    <div
      className={`${notificationType}-notification notification p8 mb8 fade-in flex-row align-c relative border-p`}
    >
      <Button
        extraClass={"button-p"}
        onClick={handleCloseNotification}
        icon={CloseIcon}
        extraStyle={{
          alignItems: "center",
          justifyContent: "center",
          position: "absolute",
          right: "4px",
        }}
      />
      <div>
        <span className="bold">{toTitleCase(notificationType)}: </span>
        <span stlye={{ marginRight: "36px" }}>
          {typeof notification.message === "string"
            ? notification.message
            : notification.statusText}
        </span>
      </div>
    </div>
  );
};

// const mapStateToProps = (state) => {
//   return {
//     notification: state.notification,
//   };
// };

const mapDispatchToProps = {
  clearNotification,
};

export default connect(null, mapDispatchToProps)(NotificationAlert);
