import React, { useEffect } from "react";
import { connect } from "react-redux";
import { Form as FinalForm, Field } from "react-final-form";
import { useHistory } from "react-router-dom";
import { loginUser } from "../reducers/userReducer";
import {
  required,
  minLength,
  isEmail,
  composeValidators,
} from "../helpers/formValidatorHelper";
import { Button } from "react-bootstrap";

const Login = ({ loginUser, username }) => {
  const history = useHistory();

  useEffect(() => {
    if (username) {
      history.push("/");
    }
  }, []);

  const onSubmit = async ({ email, password }) => {
    await loginUser(email, password);
  };

  return (
    <div className="vh100 w100 fade-in relative">
      <div className="center m8">
        <div className="login r m4 p24 bs-3">
          <h1>Login</h1>
          <FinalForm
            onSubmit={onSubmit}
            render={({ handleSubmit }) => (
              <form onSubmit={handleSubmit}>
                <h2>Email</h2>
                <Field
                  name="email"
                  validate={composeValidators(isEmail, required)}
                >
                  {({ input, meta }) => (
                    <>
                      <input
                        className="form-control"
                        placeholder="example@email.com"
                        type="email"
                        {...input}
                        required
                      />
                      {meta.error && meta.touched && (
                        <span className="form-error">{meta.error}</span>
                      )}
                    </>
                  )}
                </Field>
                <h2>Password</h2>
                <Field
                  name="password"
                  validate={composeValidators(required, minLength(3))}
                >
                  {({ input, meta }) => (
                    <>
                      <input
                        className="form-control"
                        placeholder="Password"
                        type="password"
                        {...input}
                        required
                      />
                      {meta.error && meta.touched && (
                        <span className="form-error">{meta.error}</span>
                      )}
                    </>
                  )}
                </Field>
                <Button
                  className="form-button"
                  id="form-submit"
                  type="submit"
                  variant="primary"
                >
                  Submit
                </Button>
              </form>
            )}
          />
        </div>
        <div className="mb4">Forgot your password?</div>
        <div>Try a demo account</div>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    username: state.user.data.username,
  };
};

const mapDispatchToProps = {
  loginUser,
};

export default connect(mapStateToProps, mapDispatchToProps)(Login);
