import React from "react";
import { connect } from "react-redux";
import { Form, Field } from "react-final-form";
import { createUser, updateUser, deleteUser } from "../reducers/userReducer";
import { setNotification } from "../reducers/notificationReducer";
import {
  required,
  minLength,
  isEmail,
  composeValidators,
} from "../utils/formValidatorHelper";

const Settings = ({ user, updateUser, deleteUser }) => {
  const userDetails = {
    username: user.data.username,
    oldEmail: user.data.email,
    id: user.data.id,
  };

  const handleEmailChange = async (values) => {
    const confirm = window.confirm(
      `Change email from ${userDetails.oldEmail} to ${values.newEmail}?`
    );
    if (confirm) {
      await updateUser({
        newEmail: values.newEmail,
        ...userDetails,
      });
    }
  };

  const handlePasswordChange = async (values) => {
    const { oldPassword, newPassword, checkPassword } = values;
    await updateUser({
      oldPassword,
      newPassword,
      checkPassword,
      ...userDetails,
    });
  };

  const handleDelete = async ({ password }) => {
    const confirm = window.confirm(`Delete ${userDetails.username}?`);
    if (confirm) {
      await deleteUser(password, user.id);
    }
  };

  return (
    <>
      <h1 className="f24 bold mt16 mb16">Settings</h1>
      <Form
        onSubmit={handleEmailChange}
        render={({ handleSubmit, values }) => (
          <form className="form-element" onSubmit={handleSubmit}>
            <div className="form-item">
              <h5>New Email</h5>
              <Field
                name="newEmail"
                validate={composeValidators(required, minLength(3), isEmail)}
              >
                {({ input, meta }) => (
                  <div>
                    <input
                      className="form-control"
                      placeholder="new@email.com"
                      type="email"
                      {...input}
                    />
                    {meta.error && meta.touched && (
                      <span className="form-error">{meta.error}</span>
                    )}
                  </div>
                )}
              </Field>
            </div>
          </form>
        )}
      />
    </>
  );
};

const mapStateToProps = (state) => {
  return {
    user: state.user,
  };
};

const mapDispatchToProps = {
  createUser,
  updateUser,
  deleteUser,
  setNotification,
};

export default connect(mapStateToProps, mapDispatchToProps)(Settings);
