import React from "react";
import { Field } from "react-final-form";
import { composeValidators } from "../../utils/formValidatorHelper";
import {
  developerTooltipHelper,
  occupierInvestorTooltipHelper,
} from "../../utils/tooltipHelper";

const FinalFormField = ({
  label,
  fieldName,
  type,
  validators,
  placeholder,
  fieldType,
  maxLength,
  min,
  max,
  step,
  prepend,
  append,
  parseType,
  readOnly,
}) => {
  // const header =
  //   type === "developer"
  //     ? developerTooltipHelper[input].header
  //     : occupierInvestorTooltipHelper[input].header;

  const message =
    type === "developer"
      ? developerTooltipHelper[fieldName].message
      : occupierInvestorTooltipHelper[fieldName].message;

  const parse = (value) => {
    if (parseType === "parseInt") {
      return isNaN(parseInt(value)) ? "" : parseInt(value);
    }
    return isNaN(parseFloat(value)) ? "" : parseFloat(value);
  };

  return (
    <>
      <label>{label}</label>
      <span aria-label={message} data-balloon-pos="up">
        ?
      </span>
      <Field
        name={fieldName}
        validate={composeValidators(...validators)}
        parse={parse}
      >
        {({ input, meta }) => (
          <>
            {prepend && <span>{prepend}</span>}
            <input
              className="form-control"
              placeholder={placeholder}
              type={fieldType}
              maxLength={maxLength}
              min={min}
              max={max}
              step={step}
              {...input}
              readOnly={readOnly}
            />
            {append && <span>{append}</span>}
            {meta.error && meta.touched && (
              <span className="form-error">{meta.error}</span>
            )}
          </>
        )}
      </Field>
    </>
  );
};

export default FinalFormField;
