import React from "react";
import { connect } from "react-redux";
import { hideHelperMessage } from "../../reducers/notificationReducer";

const HelperMessage = ({
  userId,
  hideHelperMessage,
  type,
  body,
  helperMessages,
}) => {
  const handleClick = (e) => {
    e.preventDefault();
    hideHelperMessage(userId, type);
  };

  console.log("helperMessages", helperMessages);

  // if (helperMessages.includes(type)) {
  //   return null;
  // } else {
  return (
    <div className="r w100 p8 bg-1 bs-2">
      <div className="mt12 mb12">{body}</div>
      <button className="form-button-p bs-3 font-white" onClick={handleClick}>
        Ok
      </button>
    </div>
  );
  // }
};

const mapStateToProps = (state) => {
  return {
    userId: state.user.data.id,
    helperMessages: state.notification.helperMessages,
  };
};

const mapDispatchToProps = {
  hideHelperMessage,
};

export default connect(mapStateToProps, mapDispatchToProps)(HelperMessage);
