import React from "react";
import { FieldArray } from "react-final-form-arrays";
import { Field } from "react-final-form";

const MortgageOverpayments = ({ push }) => {
  return (
    <FieldArray name="overPayments">
      {({ fields }) => (
        <div>
          <table>
            <thead>
              <tr>
                <th style={{ textAlign: "center" }}>#</th>
                <th>Year</th>
                <th>Overpayment</th>
              </tr>
            </thead>
            <tbody>
              {fields.map((name, index) => (
                <tr key={index}>
                  <th scope="row">{index + 1}</th>
                  <td>
                    {/* <Field
                      className="form-control"
                      name={`${name}.year`}
                      component="input"
                      placeholder="Year"
                      type="number"
                      min="0"
                    /> */}
                    <Field name="loanType" validate={required}>
                      {({ input, meta }) => (
                        <div className="relative mb20">
                          <input
                            className={`form-input w100 ${
                              prepend ? "pl32" : ""
                            } ${append ? "pr80" : ""}`}
                            placeholder="Year"
                            type="number"
                            min="0"
                            max="1000000"
                            step="1"
                          />
                          {prepend && (
                            <span className="prepend absolute f16 pl12 pt10">
                              {prepend}
                            </span>
                          )}
                          {append && (
                            <span className="append absolute f16 pr12 pt10">
                              {append}
                            </span>
                          )}
                          {meta.error && meta.touched && (
                            <span className="form-error f10">{meta.error}</span>
                          )}
                        </div>
                      )}
                    </Field>
                  </td>
                  <td>
                    <Field
                      className="form-control"
                      name={`${name}.payment`}
                      component="input"
                      placeholder="Overpayment"
                      type="number"
                      min="0"
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <button onClick={() => push("overPayments", undefined)} type="button">
            Add Overpayment
          </button>
        </div>
      )}
    </FieldArray>
  );
};

export default MortgageOverpayments;
