import React, { useState } from "react";
import { connect } from "react-redux";
import { CONSTANTS } from "../../static/constants";
import {
  developerTooltipHelper,
  occupierInvestorTooltipHelper,
} from "../../utils/tooltipHelper";

const Tooltip = ({ type, input }) => {
  const [show, setShow] = useState(false);

  const message =
    type === CONSTANTS.TYPES.DEVELOPER
      ? developerTooltipHelper[input].message
      : occupierInvestorTooltipHelper[input].message;

  return (
    <div className="pos-rel">
      <button
        type="button"
        className="tooltip-btn"
        onClick={() => setShow(!show)}
      >
        ?
      </button>
      {show && <div className="pos-abs">{message}</div>}
    </div>
  );
};

export default connect(null, null)(Tooltip);
